        let url = apiUrl + path;
        // POST de la requette 	
        return this.http.get(url, { headers: headers, params: body });
    }
    /**
     * Construction et envoi de la requette de connexion au serveur
     *     async addParticipant(authToken: AuthToken, projectId: number, email: string, name: string, company: string, jobTitle: string, role: ParticipantRole): Promise<ProjectParticipant>

     */
    addParticipant(projectId, email, name, company, jobTitle, role) {
        let headers = new HttpHeaders({
            "Access-Control-Allow-Headers": "Content-Type, Authorization, Content-Length, X-Requested-With",
            "Access-Control-Allow-Origin": "*",
            "Content-Type": "application/json"
        });
        // Création d'un post pour demander un login.
        let body = {
            authToken: this.userData.authToken,
            projectId: projectId,
            email: email,
            name: name,
            company: company,
            jobTitle: jobTitle,
            role: role
        };
        let apiUrl = HttpService.getAPIUrl();
        let path = HttpService.getPathByWebServiceName('user');
        let url = apiUrl + path;
        // POST de la requette 	
        return this.http.post(url, body, { headers: headers, });
    }
}
